<template>
  <div>

    <Head>
      <span>
        {{msg}}
      </span>
    </Head>
    <section class="f_top">
      <div>
        <p>支付剩余时间</p>
        <p>{{tim}}</p>
      </div>
    </section>
    <p class="f_center_top">选择支付方式</p>
    <section>
      <div class="f_center_bottom">
        <div>
          <img src="data:image/png;base64,"
            alt="">
          <span>支付宝</span>
        </div>
        <span :class="{blue:bol1}" class="el-icon-circle-check" @click="bol1=true"></span>
      </div>
    </section>
    <section>
      <div class="f_center_bottom" >
        <div>
          <img src="data:image/png;base64,"
            alt="">
          <span>微信</span>
        </div>
        <span :class="{blue:!bol1}" class="el-icon-circle-check" @click="bol1=false"></span>
      </div>
    </section>
    <div class="f_submit" @click="bol2=true">
      确认支付
    </div>
<Alert v-if="bol2">
        <p>{{content}}</p>
        <div class="f_alert_submit" @click="tiao" >确认</div>
</Alert>
<Alert v-if="bol">
        <p>支付超时</p>
        <div class="f_alert_submit" @click="bol=false" >确认</div>
</Alert>

  </div>

</template>


<script>
  import Head from "../../header.vue"
  import Alert from '../../alert.vue'
  export default {
    name: "zhifu",
    data() {
      return {
        msg: "在线支付",
       content:"当前环境无法支付,请打开官方APP进行付款",
        bol:false,
        bol1:true,
        bol2:false,
        tim:"00:15:00"
      }
    },
    components: {
      Head,
      Alert
    },
    created() {
       var _this = this;
      var time = 900;
      clearInterval(timer);
      var timer = setInterval(function () {
        time--;
        if (time > 0) {
          var m = parseInt(time / 60);
          if (m < 10) {
            m = '0' + m;
          }
          var s = Math.floor(time % 60);
          if (s < 10) {
            s = '0' + s;
          }
          _this.tim = '00:' + m + ':' + s;
          
        } else {
            _this.tim = "00:00:00";
            _this.bol = true;
          clearInterval(timer);
        }
      }, 1000);
    },
    methods:{
        tiao(){
            this.bol2 = false;
             this.$router.push('/order');
        }
    }
    
  }

</script>


